mapper.select:
SELECT b.* FROM ( SELECT @RNUM:=@RNUM+1 AS ROWNUM , a.* FROM (SELECT @RNUM:=0) R , (SELECT * FROM hcc_fmb_tb
where fmb_status = 'Y' AND fmb_type ='D'
order by fmb_seq DESC ) a ) as b where ROWNUM between #{param1} and #{param2}

mapper.update:
update 
  hcc_fmb_tb 
set fmb_title = #{param2}
,   fmb_content = #{param3}
,   fmb_upd_date = NOW() 

where fmb_seq = #{param1}

UPDATE hcc_fmb_tb
set fmb_cnt = fmb_cnt + 1
WHERE fmb_seq = #{param1}
