mapper.select:
select user_name
from hcc_user_tb

	where user_id=#{value}

mapper.insert:
insert into
hcc_user_tb
(
user_id
, user_pw
, user_gender
, user_name
, user_birth
, user_phone
, user_email
)
values
(
#{user_id}
, #{user_pw}
, #{user_gender}
, #{user_name}
, #{user_birth}
,
#{user_phone}
, #{user_email}
);
